trigger.body:
v_status VARCHAR2(60);
BEGIN

IF AD_isTriggerEnabled()='N' THEN RETURN;END IF;
	--while deleting the update totalpayment as difference of current amount

	IF DELETING THEN
	select po.Docstatus into v_status from efin_budget_transfertrx po left join efin_budget_transfertrxline pl on po.efin_budget_transfertrx_id = pl.efin_budget_transfertrx_id
	where po.efin_budget_transfertrx_id = :old.efin_budget_transfertrx_id;
	if(v_status<>'DR' and v_status<> 'RW' ) then
        raise exception '%','@Efin_transfer_delete@';
        end if;

	END IF;
	END EFIN_BUDGETREV_LINEDEL_TRG
