AD_VAL_RULE.CODE:
ad_org_id in 
 (select ad_org_id from c_validcombination where c_salesregion_id = @BCP_SalesregionID@ and account_id =
 @AccountId@)

C_Project_ID = (select 
		case when em_efin_projacct = 'Y' then (select case when em_efin_project_id is null
			then null else em_efin_project_id end
			from c_elementvalue 
			join c_project pro on pro.c_project_id=em_efin_project_id 
			where c_elementvalue_id = @C_ElementValue_ID@ and pro.ad_org_id in (@AD_Org_ID@,'0'))
		else (select case when em_efin_project_id is not null
		  then null
		  else (select c_project_id from c_project where em_efin_isdefault = 'Y' and ad_client_id = @AD_Client_ID@)
		  end
		 from c_elementvalue  where c_elementvalue_id = @C_ElementValue_ID@) 
		end
	from c_elementvalue  where c_elementvalue_id = (
		select c_elementvalue_id from efin_budget  where efin_budget_id  =@Efin_Budget_ID@
	)
)
